import React from "react";
import Newnote from "./components/Newnote";
import Notes from "./components/Notes";

const App = () => {
  return (
    <div>
      <Newnote />
      <div>
        all
        <input
          type="radio"
          name="filter"
          onChange={() => filterSelected("ALL")}
        />
        important
        <input
          type="radio"
          name="filter"
          onChange={() => filterSelected("IMPORTANT")}
        />
        nonimportant
        <input
          type="radio"
          name="filter"
          onChange={() => filterSelected("NONIMPORTANT")}
        />
      </div>
      <Notes />
    </div>
  );
};

export default App;
